// Global//

body {
  height: 100%;
  // background: rgb(251, 117, 172);
  background-color: rgba(245, 241, 241, 1);
  // background: linear-gradient(90deg, rgba(225, 147, 177, 0.849) 4%, rgba(245, 241, 241, 1) 4%);
  line-height: 1.5;
}

.wrapper {
  max-width: 1200px;
  display: flex;
  margin: 0 auto;
  justify-content: center;
}

.light-shade-color {
  color: #f7f3f0;
}

.serif {
  font-family: "Vidaloka", serif;
}

.sans {
  font-family: "Raleway", sans-serif;
}

.dark-title {
  font-family: "Vidaloka", serif;
  color: #3f3f3f;
  font-size: 6.7vw;
}

.dark-para {
  font-family: "Raleway", sans-serif;
  color: #3f3f3f;
  font-size: 18px;
}

.title-head,
.para {
  text-align: center;
  width: 100%;
}

.main-header,
section {
  padding: 105px 20px;
}

// End of Global //
// Specific Starts//

//HEADER
.icons {
  display: none;
}

.nav-desktop {
  display: none;
}

header {
  width: 100%;
}
.header {
  // display: block;
  width: 100%;
  background-image: url(../img/banner-background.jpg);
  height: auto;
  background-position: center;

  .main-header {
    display: flex;
    align-items: center;
    width: 100%;
    flex-wrap: wrap;
    background-color: rgba(226, 148, 178, 0.849);
    padding-top: 30px;

    .header-info {
      display: flex;
      flex-grow: 1;
      align-items: center;
      justify-content: space-between;
      width: 100%;
      padding: 20px 0;

      .name {
        font-size: 26px;
        font-weight: 900;
      }

      .slogan {
        font-size: 18px;
      }
    }

    .banner {
      width: 100%;
      flex-wrap: wrap;
      text-align: center;
      padding: 60px 0;

      .title {
        width: 100%;
        font-size: 30px;
        font-weight: 900;
      }

      .info {
        width: 100%;
        font-size: 18px;
      }
    }

    .logo {
      justify-content: flex-start;
    }

    .menu {
      display: none;
    }
  }

  .menu-button {
    background: none;
    border: none;
    svg {
      height: 30px;
      width: 30px;
    }
  }
}

// BODY//

section {
  .sect-info {
    width: 100%;

    .sm-flower {
      height: 40px;
      text-align: center;
      img {
        height: 30px;
      }
    }
  }
  p {
    padding: 10px 0;
  }
}

.services {
  background-image: url(../img/services-cropped.jpg);
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  // background-attachment: fixed;
  width: 100%;
  height: auto;

  .service-cards {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    text-align: center;
    align-items: center;

    .service-card-1 {
      max-width: 350px;
      height: 300px;
      display: block;
      margin: 20px;
      background-image: url(../img/services-decoration.jpg);
      background-size: cover;
      background-position: bottom;
    }

    .service-card-2 {
      max-width: 350px;
      height: 300px;
      display: block;
      margin: 20px;
      background-image: url(../img/services-floral-design.jpg);
      background-size: cover;
      background-position: center;
    }

    .service-card-3 {
      max-width: 350px;
      height: 300px;
      display: block;
      margin: 20px;
      background-image: url(../img/service-events.jpg);
      background-size: cover;
      background-position: center;
    }

    .card-info {
      display: flex;
      flex-wrap: wrap;
      align-items: center;
      justify-content: center;
      background-color: rgba(52, 53, 51, 0.678);
      padding: 24px;
      height: 100%;
    }
  }

  .title {
    font-size: 24px;
  }
}

// MEDIA//

@media only screen and (max-width: 960px) {
  //  HEADER//
  .main-header,
  section {
    padding: 30px 20px;
  }
}

@media only screen and (min-width: 630px) {
  .dark-title {
    font-size: 42px;
  }
}

@media only screen and (max-width: 360px) {
  .dark-title {
    font-size: 24px;
  }
}

@media only screen and (min-width: 801px) {
  //  HEADER//

  .menu-button {
    display: none;
  }
  .icons {
    display: flex;
    justify-content: flex-end;
    width: 100%;
    background-color: rgba(245, 241, 241, 1);
    padding: 5px 20px;
    height: 30px;

    svg {
      height: 20px;
      width: 20px;
      margin: 0 10px;
      fill: rgba(226, 148, 178, 0.849);
    }
  }

  .nav-desktop {
    display: block;
    max-width: 60%;
    nav {
      display: block;
      font-size: 18px;
      color: #f7f3f0;

      ul {
        display: flex;
        flex-wrap: wrap;
        justify-content: center;

        a {
          text-decoration: none;
          list-style: none;
          padding: 0 5px;
          margin: 0 2px;
        }
      }
    }
  }
}

